mapper.select:
SELECT t_product.id, t_product.distribution_price AS distributionPrice, t_product.category_id AS categoryId, t_product.brand_id AS brandId, t_product.NAME, t_product.short_name AS shortName, t_product.cover, t_product.stock_balance AS stockBalance, t_product.sales, t_product.STATUS, t_product.created_date AS createdDate, t_product.last_modified_date AS lastModifiedDate, t_product.unit, t_product.price, t_product.cost_price AS costPrice, t_product.suggested_price AS suggestedPrice, t_product.promoted, t_product.freight, t_product.free_shipping AS freeShipping, t_product.sort_order AS sortOrder, t_product.partner_level_zone AS partnerLevelZone, t_product.view_count AS viewCount, t_product.fare_id AS fareId, t_product.store_location AS storeLocation, t_product.weight, t_product.bulk, t_product.sku_id AS skuId, t_product.sku_name AS skuName, t_product.sku_code AS skuCode, t_product.banner, t_product.barcode AS barcode, t_product.org_id AS orgId,
t_product.mid, t_product.allow_coupon AS allowCoupon, t_product.credit, t_product.is_virtual AS isVirtual, t_product.required_participate_exam AS requiredParticipateExam,
t_fare_template.name AS fareName, t_product_brand.NAME AS brandName, category.NAME AS categoryName, t_supplier.name as supplierName FROM t_product LEFT JOIN t_product_brand ON t_product.brand_id = t_product_brand.id LEFT JOIN t_product_category category ON category.id = t_product.category_id LEFT JOIN t_fare_template ON t_product.fare_id = t_fare_template.id LEFT JOIN t_supplier on t_supplier.org_id = t_product.org_id LEFT JOIN t_product_specification on t_product_specification.product_id = t_product.id WHERE t_product.id = #{id}
